    // console.log("dfffff", this.state.fileUp);
    let next = this.setState({
      ver1: false,
      ver2: false,
      ver3: false,
      ver4: true,
      ver5: false,
    });
    fetch(
      `https://bridgegatebackend.herokuapp.com/api/verify/verificationrequest`,
      {
        mode: "cors",
        method: "POST",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          id: this.state.user.user.user._id,
          name: this.state.name,
          address: this.state.address,
          phoneNumber: this.state.phoneNumber,
          documentName: this.state.DocumentFile,
          documentFile: this.state.doc,
          Img: this.state.docPass,
          proofDocument: this.state.docProof,
        }),
      }
    )
      .then(function (res) {
        if (res.ok) {
          message.success("Your verification request was successfully made");
          //  this.hVer1()
          return next;
        } else {
          message.error("Your request was not successful");
        }
      })
      .then((data) => {
        this.setState({ verifyS: true, VView: false });

        if (data) {
          // this.setState({ verifyS: true, VView: false });
          this.setState({
            ver1: false,
            ver2: false,
            ver3: false,
            ver4: true,
            ver5: false,
          });
        } else {
          console.log("bad", data);
        }
      });
