                      <th>P&L</th>
                      <th>PROFIT/LOSS</th>
                      <th>STATUS</th>
                    </tr>
                    {this.props.orders.length > 0
                      ? this.props.orders.map((item, i) => (
                          <tr>
                            <td>
                              {item.time ? item.time.slice(0, 10) : ""}{" "}
