          termsOfServices: "test",
          newUserWelcomeMailTitle: "",
          newUserWelcomeMail: "test",
          activateUserAccountMail: "test",
          adminWithdrawRequestMail: "test",
          confirmWithdrawRequestMail: "test",
          wthdrawRequestProcessedMail: "test",
          userResetPasswordMail: "test",
          masterCardStatus: true,
          bitCoinStatus: true,
          userResetPasswordConfirmationMail: "test",
          userSubscriptionEExpirationMail: "test",
          userNewIPDetectedMail: "test",
          btcHeaderText: "Address",
          btcAddress: "bitconjsudiud",
          buyBTCLink: "lunhdd",
          loginBackgroundColor: "",
          loginButtonColor: "",
          loginBackgroundImage:
            "http://res.cloudinary.com/code-idea/image/upload/v1616235704/s5ig4ra9dmzkmko1vgcg.svg",
          siteLogoWhite: "",
          appDescription: "test",
          maintenanceMode: true,
          numberFormat: "",
          currencyPosition: "",
          switchChartTradingView: false,
          allowUsersswitchView: true,
          disableChart: false,
          enableGoogleAds: false,
