          enableDonation: false,
          enableAutoLanguage: false,
          enablePOEEditorLanguage: false,
          donationAddress: "test",
          donationText: "",
          startWatchUserSignUp: "test",
          newIdentityTitle: "",
          newIdentityWizardAdvert: "",
          startWatchNewUserSignUp: "",
          enableCookiePopupTitle: "",
          blacklistedCountries: ["Afghanistan"],
          cookiePopupText: "test",
          cookiePopupTitle: "test",
          googleSlot: "test",
          googleAdClient: "test",
          googleAnalytics: "test",
          POEditorAPIKeys: "",
          POEditorLanguage: "",
          siteLanguage: "",
          siteStartingCurrencyPair: "",
          yourMainColor: "royalblue",
          siteMenuType: "",
          showFooterBar: false,
          defaultThemeColor: "",
          userCanChangeTheme: false,
          showTimeInFooter: false,
          showContactInFooter: false,
          showCalcaulator: true,
          loginCarouselImage1:
            "http://res.cloudinary.com/code-idea/image/upload/v1616235703/fcf35pgn15691hbwktkd.jpg",
          loginCarouselImage2:
            "http://res.cloudinary.com/code-idea/image/upload/v1616235704/euv0czvuucoptcpddifr.jpg",
          loginCarouselImage3:
            "http://res.cloudinary.com/code-idea/image/upload/v1616235704/fpx7lotco198eez4k0yh.jpg",
          allowsignup: true,
          phoneNumberRequired: false,
          userNeedVerifyAccount: false,
          needCaptchaSignup: false,
          signWithGoogle: false,
          signInFacebook: false,
          googleRecaptchaSiteKey: "",
