          enableIdentitySystem: true,
          blockTradingWithoutIdentity: false,
          blockDepositeWithoutIdentityVerification: false,
          blockWithdrawWithoutIdentity: false,
          newIdentityWizardTitle: "",
          newIdentityStartButton: "",
          identityStepName: "",
          identityStepType: "",
          identityStepDescription: "",
          enableIdentityUploadWithWebCam: "",
          webCamDocumentRatio: "",
          identityDocumentName: "",
          documentIdentityList: "",
          paymentSuccessText: "test",
          paymentRefPattern: "tessst",
          paymentNeedsApproval: true,
          verificationDecinedNotificationemplate: "",
          verificationApprovedNotificationemplate: "",
          paymentMethods: [],
          _id: "6051fe8435e8702a5a8a0957",
          time: "2021-03-17T13:05:08.772Z",
          __v: 2,
          siteFav:
            "http://res.cloudinary.com/code-idea/image/upload/v1616195960/cxwjyomq8dgph9j8cjhb.jpg",
          siteTitle: "proweb Trader",
        }
  );





  useEffect(() => {
    (async () => {
      let resp = await fetch(
        `https://bridgegatebackend.herokuapp.com/api/site`
      );
      let web = await resp.json();
      setWeb({
        web,
      });
      fav.href =  web.siteFav 
      title.innerHTML = web.siteTitle 
      let b = { web };
      localStorage.setItem("web", JSON.stringify(b));
    })();
  }, [web]);

  const handle1L = ({ target }) => {
    setData((state) => ({
      ...state,
      email: target.value,
    }));
  };

  const handle2L = ({ target }) => {
    setData((state) => ({
      ...state,
      password: target.value,
    }));
  };

  const handle1R = ({ target }) => {
    setData((state) => ({
      ...state,
      email: target.value,
    }));
  };

  const handle2R = ({ target }) => {
    setData((state) => ({
      ...state,
      password: target.value,
    }));
  };

  const handle3R = ({ target }) => {
    setData((state) => ({
      ...state,
      repeatPassword: target.value,
    }));
  };

  const handle4R = ({ target }) => {
    setData((state) => ({
      ...state,
      name: target.value,
    }));
  };

  let handleSubmitL = (e) => {
    e.preventDefault();
    console.log("in submit", data);

    fetch("https://bridgegatebackend.herokuapp.com/api/login", {
      mode: "cors",
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    })
      .then(function (res) {
        return res.json();
      })
      .then((data) => {
        if (data.user) {
          setUser((state) => ({
            ...state,
            user: data,
          }));

          let a = { user: data };

          localStorage.setItem("user", JSON.stringify(a));

          history.push("/dashboard");

          console.log("good", data);
        } else {
          console.log("bad");

          setError("incorrect details");
        }
      });
  };

  let handleSubmitR = (e) => {
    e.preventDefault();
    console.log("in submit", data);

    fetch("https://bridgegatebackend.herokuapp.com/api/registration", {
      mode: "cors",
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    })
      .then(function (res) {
        return res.json();
      })
      .then((data) => {
        if (data) {
          setUser((state) => ({
            ...state,
            user: data,
          }));
          console.log("good", data);

          let a = { user: data };

          localStorage.setItem("user", JSON.stringify(a));

          setError("you can login now ");

          history.push("/");
        } else {
          setError("incorrect details");
        }
      });
  };

  return (
    <div className="App">
      <Switch>
        <Route exact path="/">
          <Login
            handleSubmitL={handleSubmitL}
            error={error}
            handle1L={handle1L}
            handle2L={handle2L}
            site={site}
            web={web}
          />
        </Route>
        <Route path="/forgot_password">
          <ForgotPassword site={site} web={web} />
        </Route>
        <Route path="/signup">
          <SignUp
            error={error}
            handleSubmitR={handleSubmitR}
            handle1R={handle1R}
            handle2R={handle2R}
            handle3R={handle3R}
            handle4R={handle4R}
            site={site}
            web={web}
          />
        </Route>
        <Route path="/dashboard">
          <Dashboard orderIsh={orderIsh} user={user} site={site} web={web} />
          <Foot />
        </Route>

        <Route path="/port">
        <NavbarCo orderIsh={orderIsh} user={user} site={site} web={web} />

          <Port user={user} site={site} web={web} />
          <Foot />
        </Route>

        <Route path="/board">
          <Dashboard user={user} site={site} web={web} />
          <Foot />
        </Route>

        <Route path="/market">
          <NavbarCo user={user} site={site} web={web} />
          <Market user={user} site={site} web={web} />
          <Foot />
        </Route>

        <Route path="/manager">
        <NavbarCo user={user} site={site} web={web} />

          <Manager user={user} site={site} web={web} />
          <Foot />
        </Route>

        {/* admin */}
        <Route path="/admin">
        <NavbarCo user={user} site={site} web={web} />

          <Admin user={user} site={site} web={web} />
          <Foot />
        </Route>

        <Route path="/report">
        <NavbarCo orderIsh={orderIsh} user={user} site={site} web={web} />

          <Report user={user} site={site} web={web} />
          <Foot />
        </Route>

        <Route path="/bitcoin">
        <NavbarCo user={user} site={site} web={web} />

          <Bitcoin user={user} site={site} web={web} />
          <Foot />
        </Route>

        <Route path="/mastercard">
        <NavbarCo user={user} site={site} web={web} />

          <MasterCard user={user} site={site} web={web} />
          <Foot />
        </Route>

        <Route path="/general-settings">
        <NavbarCo user={user} site={site} web={web} />

          <General user={user} site={site} web={web} />
          <Foot />
        </Route>

        <Route path="/trading">
        <NavbarCo orderIsh={orderIsh} user={user} site={site} web={web} />

          <Trading user={user} site={site} web={web} />
          <Foot />
        </Route>

        <Route path="/bank-accounts">
        <NavbarCo user={user} site={site} web={web} />

          <Bank user={user} site={site} web={web} />
          <Foot />
        </Route>

        <Route path="/search">
        <NavbarCo orderIsh={orderIsh} user={user} site={site} web={web} />

          <Search user={user} site={site} web={web} />
          <Foot />
        </Route>

        <Route path="/payment">
        <NavbarCo user={user} site={site} web={web} />

          <Payment user={user} site={site} web={web} />
          <Foot />
        </Route>

        <Route path="/subscription">

        <NavbarCo user={user} site={site} web={web} />

          <Subscription user={user} site={site} web={web} />
          <Foot />
        </Route>

        <Route path="/identity">
        <NavbarCo orderIsh={orderIsh} user={user} site={site} web={web} />

          <Identity user={user} site={site} web={web} />
          <Foot />
        </Route>

        <Route path="/new-social">
        <NavbarCo user={user} site={site} web={web} />

          <New user={user} site={site} web={web} />
          <Foot />
        </Route>

        <Route path="/login-signup">
        <NavbarCo user={user} site={site} web={web} />

          <LoginSignup user={user} site={site} web={web} />
          <Foot />
        </Route>

        <Route path="/intro">
        <NavbarCo user={user} site={site} web={web} />

          <Intro user={user} site={site} web={web} />
          <Foot />
        </Route>

        <Route path="/additional-pages">
        <NavbarCo user={user} site={site} web={web} />

          <Additional user={user} site={site} web={web} />

          <Foot />
        </Route>

        <Route path="/general-appearance">
        <NavbarCo user={user} site={site} web={web} />

          <GeneralApp user={user} site={site} web={web} />

          <Foot />
        </Route>

        <Route path="/login-page">
        <NavbarCo user={user} site={site} web={web} />

          <LoginApp user={user} site={site} web={web} />

          <Foot />
        </Route>

        <Route path="/mail-settings">
        <NavbarCo user={user} site={site} web={web} />

          <MailS user={user} site={site} web={web} />

          <Foot />
        </Route>

        <Route path="/templates">
        <NavbarCo user={user} site={site} web={web} />

          <Templates user={user} site={site} web={web} />

          <Foot />
        </Route>
      </Switch>
    </div>
  );
}

export default App;
