import React, { useState } from "react";
import Favicon from "react-favicon";
import { Eye, EyeSlash } from "react-bootstrap-icons";

import { Link } from "react-router-dom";
import ImageCarousel from "../Component/ImageCarousel/ImageCarousel";
import { Container, Row, Col, Form, Button } from "react-bootstrap";
import Header from "../Component/Header/Header";
import Footer from "../Component/Footer/Footer";
import "../AccountsAsset/form.css";

export default function Login(props) {
  let fav = document.querySelector("#favicon");

  const [passwordShown, setPasswordShown] = useState(false);
  let web = props.web.web;
  let web2 = props.web;
  if (fav) {

    fav.href = web.siteFav !== undefined? web.siteFav :'';
    fav.href = web2.siteFav !== undefined? web.siteFav :'';

    console.log(fav, web.siteFav, "Limportant");x 
  }

  let savedSite = JSON.parse(localStorage.getItem("site"))
    ? JSON.parse(localStorage.getItem("site"))
    : [];

  const togglePasswordVisiblity = () => {
    setPasswordShown(passwordShown ? false : true);
  };

  return (
    <div
      style={{
        background: `url(${web ? web.loginBackgroundImage : ""})`,
        backgroundRepeat: "no-repeat",
      }}
    >
      <Favicon url={web ? web.siteFav : ""} />
      <Favicon url={web2 ? web2.siteFav : ""} />

      <Container>
        <Row>
          <Col xs={12} md={5} style={{ paddingRight: 0 }}>
            <section className="auth-wrapper">
              <Header web={web} savedSite={savedSite} />
              <Form className="mt-5 pt-5" onSubmit={props.handleSubmitL}>
                {props.error ? (
                  props.error.length > 0 ? (
                    <p className="error">{props.error}</p>
                  ) : (
                    props.error
                  )
                ) : (
                  ""
                )}
                <Form.Group
                  controlId="formBasicEmail"
                  className="floating mb-0"
                >
                  <Form.Control
                    type="email"
                    name="email"
                    onChange={props.handle1L}
                  />
                  <Form.Label>Your e-mail address</Form.Label>
                </Form.Group>
                <Form.Group
                  controlId="formBasicPassword"
                  className="floating mb-3"
                >
                  <Form.Control
                    type={passwordShown ? "text" : "password"}
                    name="password"
                    onChange={props.handle2L}
                    style={{ borderBottom: "1px solid #d8dbe4" }}
                  />
                  <Form.Label>Your password</Form.Label>
                  {passwordShown ? (
                    <EyeSlash
                      className="eye"
                      onClick={togglePasswordVisiblity}
                    />
                  ) : (
                    <Eye className="eye" onClick={togglePasswordVisiblity} />
                  )}
                </Form.Group>
                <div className="d-flex align-items-center justify-content-between pb-3">
                  <div>
                    <Link to="/forgot_password" className="backtologin">
                      Forgot Password?
                    </Link>
                  </div>
                  <Button
                    variant="primary"
                    style={{ background: web ? web.yourMainColor : "" }}
                    type="submit"
                  >
                    LOGIN
                  </Button>
                </div>
              </Form>
              <div>
                <div className="divider text-center">
                  <hr />
                  <p>Or</p>
                </div>
                <Link to="/signup" className="btn btn-primary btn-create">
                  Create a new account
                </Link>
              </div>
            </section>
          </Col>
          <Col xs={12} md={7} style={{ paddingLeft: 0 }}>
            <ImageCarousel web={web} />
          </Col>
        </Row>
        <Footer />
      </Container>
    </div>
  );
}
