import React, { useState, useEffect } from "react";
import "./App.css";
import { Route, Switch, useHistory } from "react-router-dom";
import Login from "./Pages/Login";
import SignUp from "./Pages/SignUp";
import Dashboard from "./Pages/Dashboard";
import Port from "./Pages/Port";
import Market from "./Pages/Market";
import Manager from "./Pages/Manager";
import Admin from "./Pages/Admin";
import Report from "./Pages/Report";
import MasterCard from "./Pages/MasterCard";
import Bitcoin from "./Pages/Bitcoin";
import GeneralApp from "./Pages/GeneralApp";
import LoginApp from "./Pages/LoginApp";
import General from "./Pages/General";
import Trading from "./Pages/Trading";
import Bank from "./Pages/Bank";
import Search from "./Pages/Search";
import Payment from "./Pages/Payment";
import Subscription from "./Pages/Subscription";
import Identity from "./Pages/Identity";
import New from "./Pages/New";
import LoginSignup from "./Pages/LoginSignup";
import Intro from "./Pages/Intro";
import Additional from "./Pages/Additional";
import Foot from "./Component/Foot";
import ForgotPassword from "./Pages/ForgotPassword";
import Templates from "./Pages/Templates";
import MailS from "./Pages/MailS";
import { NavbarCo } from "./Component/Nav";
import Favicon from "react-favicon";

function App() {
  let fav = document.getElementById("favicon");
  let title = document.getElementById("title");
  const history = useHistory();
  let savedUser = JSON.parse(localStorage.getItem("user"));
  let savedSite = JSON.parse(localStorage.getItem("site"));
  let saveWeb = JSON.parse(localStorage.getItem("web"));
  let [user, setUser] = useState(savedUser ? savedUser.user : []);
  const [orderIsh, setOrderIsh] = useState("");

  const [site, setSite] = useState(savedSite ? savedSite : []);
  const [data, setData] = useState([]);
  const [error, setError] = useState("");

  const [web, setWeb] = useState(saveWeb ? saveWeb : []);

  useEffect(() => {
    (async () => {
      let response = await fetch(
        `https://bridgegatebackend.herokuapp.com/api/site`
      );
      let data = await response.json();
      setSite({
        site: data,
      });
      let a = { site: data };
      localStorage.setItem("site", JSON.stringify(a));
    })();
  }, [site]);

  // useEffect(() => {
  //   (async () => {
  //     let response = await fetch(
  //       `https://bridgegatebackend.herokuapp.com/api/trade/user/${user.user.user._id}`
  //     );
  //     user = await response.json();
  //     if (user !== undefined){
  //       setUser({
  //         user: user,
  //       });
  //       console.log(user.user.user._id,'usss')
  //     }
  //     console.log(user.user.user._id,'1')

  //     let a = { user: user };
  //     localStorage.setItem("user", JSON.stringify(a));
  //   })();
  // },[user]);

  useEffect(() => {
    (async () => {
      let resp = await fetch(
        `https://bridgegatebackend.herokuapp.com/api/site`
      );
      let web = await resp.json();
      setWeb({
        web,
      });
      // fav.href =  web.siteFav
      title.innerHTML = web.siteTitle;
      let b = { web };
      localStorage.setItem("web", JSON.stringify(b));
    })();
  }, [web]);

  const handle1L = ({ target }) => {
    setData((state) => ({
      ...state,
      email: target.value,
    }));
  };

  const handle2L = ({ target }) => {
    setData((state) => ({
      ...state,
      password: target.value,
    }));
  };

  const handle1R = ({ target }) => {
    setData((state) => ({
      ...state,
      email: target.value,
    }));
  };

  const handle2R = ({ target }) => {
    setData((state) => ({
      ...state,
      password: target.value,
    }));
  };

  const handle3R = ({ target }) => {
    setData((state) => ({
      ...state,
      repeatPassword: target.value,
    }));
  };

  const handle4R = ({ target }) => {
    setData((state) => ({
      ...state,
      name: target.value,
    }));
  };

  let handleSubmitL = (e) => {
    e.preventDefault();
    console.log("in submit", data);

    fetch("https://bridgegatebackend.herokuapp.com/api/login", {
      mode: "cors",
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    })
      .then(function (res) {
        return res.json();
      })
      .then((data) => {
        if (data.user) {
          setUser((state) => ({
            ...state,
            user: data,
          }));

          let a = { user: data };

          localStorage.setItem("user", JSON.stringify(a));

          history.push("/dashboard");

          console.log("good", data);
        } else {
          console.log("bad");

          setError("incorrect details");
        }
      });
  };

  let handleSubmitR = (e) => {
    e.preventDefault();
    console.log("in submit", data);

    fetch("https://bridgegatebackend.herokuapp.com/api/registration", {
      mode: "cors",
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    })
      .then(function (res) {
        return res.json();
      })
      .then((data) => {
        if (data) {
          setUser((state) => ({
            ...state,
