import React, { useState, useEffect, Component } from "react";
import Favicon from "react-favicon";
import "../AccountsAsset/dash.css";
import { Route, Switch, useHistory, Redirect, NavLink } from "react-router-dom";
import Sidebar from "../Component/Sidebar";
import Moment from "react-moment";
import "moment-timezone";
import TradingViewWidget, { Themes } from "react-tradingview-widget";
import { message } from "antd";
import { Modal, Button } from "antd";
import VerifyDocModal from "../Component/VerifyModal";
import Router from "../Component/Router";
import { Container, Form, Row, Col } from "react-bootstrap";
import WithdrawDetailsModal from "../Component/WithdrawDetailsInfoPopOver";
import VerifyDetailsPopOver from "../Component/VerifyDetailsPopOver";
import PaymentDetailsPopOver from "../Component/PaymentDetailsPopOver";
import { NavbarCo } from "../Component/Nav";

class Manager extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: this.props.user,
      redirect: "/",
      data: {},
      orders: [],
      disA1: true,
      disA2: true,
      btcP: [],
      market: false,
      board: false,
      port: false,
      manager: true,
      levIsh: false,
      admin: false,
      allUsers: [],
      allVerify: [],
      allDeposit: [],
      allWithdraw: [],
      allBankTransfor: [],
      allTrades: [],
      userId: {},
      displayC: false,
      curDeposit: [],
      card: true,
      bal: false,
      payments: false,
      secu: false,
      with: false,
      numVerified: 0,
      orderT: false,
      updateAmount: "",
      text: "Save",
      decline: false,
      autoTrade: false,
      declinedMessage: "",
      yourName: "",
      yourEmailAddress: "",
      yourLanguage: "",
      yourCurrency: "",
      userLevel: "",
      yourWallet: 0,
      editProfile: false,
    };

    this.myRef3 = this.props.user.user ? React.createRef() : "";
    this.textInput = this.props.user.user ? React.createRef() : "";
  }

  token = "pk_135c1daf1b8d4130b9318fd5e8ab0e5e";
  fav = document.getElementById("favicon");
  title = document.getElementById("title");
  // state = {
  //   loading: false,
  //   visible: false,
  // };

  setDeclinedMessage = (e) => {
    this.setState({ declinedMessage: e.target.value });
  };
  showDeclineIdentity = () => {
    this.setState({
      decline: true,
    });
  };

  closeDeclineIdentity = () => {
    this.setState({
      decline: false,
    });
  };

  setCard = () => {
    this.setState({
      card: true,
      bal: false,
      payments: false,
      secu: false,
      with: false,
      orderT: false,
    });

    (async () => {
      let response = await fetch(
        `https://bridgegatebackend.herokuapp.com/api/trade/desposit${
