import React, { useState, useEffect, Component } from "react";
import "../AccountsAsset/dash.css";
import { Route, Switch, useHistory, Redirect, NavLink } from "react-router-dom";
import Sidebar from "../Component/Sidebar";
import Moment from "react-moment";
import "moment-timezone";
import TradingViewWidget, { Themes } from "react-tradingview-widget";
import { message } from "antd";
import { Modal, Button } from "antd";
import VerifyDocModal from "../Component/VerifyModal";
import Router from "../Component/Router";
import { Container, Form, Row, Col } from "react-bootstrap";
import WithdrawDetailsModal from "../Component/WithdrawDetailsInfoPopOver";
import VerifyDetailsPopOver from "../Component/VerifyDetailsPopOver";
import PaymentDetailsPopOver from "../Component/PaymentDetailsPopOver";
class Manager extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: this.props.user,
      redirect: "/",
      data: {},
      orders: [],
      disA1: true,
      disA2: true,
      btcP: [],
      market: false,
      board: false,
      port: false,
      manager: true,
      levIsh: false,
      admin: false,
      allUsers: [],
      allVerify: [],
      allDeposit: [],
      allWithdraw: [],
      allBankTransfor: [],
      allTrades: [],
      userId: {},
      displayC: false,
      curDeposit: [],
      card: true,
      bal: false,
      payments: false,
      secu: false,
      with: false,
      numVerified: 0,
      orderT: false,
      updateAmount: "",
      text: "Save",
      decline: false,
      autoTrade: false,
      declinedMessage: "",
      yourName: "",
      yourEmailAddress: "",
      yourLanguage: "",
      yourCurrency: "",
      userLevel: "",
      editProfile: false,
