import React, { useState, useEffect, Component } from "react";
import Favicon from "react-favicon";
import "../AccountsAsset/dash.css";
import { Route, Switch, useHistory, Redirect, NavLink } from "react-router-dom";
import Sidebar from "../Component/Sidebar";
import AdminSidebar from "../Component/AdminSidebar";
import TradingViewWidget, { Themes } from "react-tradingview-widget";

class AutoTrading extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: this.props.user,
      redirect: "/",
      data: {},
      orders: [],
      disA1: true,
      disA2: true,
      btcP: [],
      market: false,
      board: false,
      port: false,
      manager: false,
      levIsh: false,
      admin: true,

      allUsers: [],
      allVerify: [],
      allDeposit: [],
      allWithdraw: [],
      allTrades: [],
      userId: {},
      displayC: false,
      curDeposit: [],
      card: true,
      bal: false,
      payments: false,
      secu: false,
      with: false,
      orderT: false,
      updateAmount: "",
    };
    this.myRef3 = this.props.user.user ? React.createRef() : "";
    this.textInput = this.props.user.user ? React.createRef() : "";
  }
  token = "pk_135c1daf1b8d4130b9318fd5e8ab0e5e";
  fav = document.getElementById("favicon");
  title = document.getElementById("title");

  componentDidMount() {
    (async () => {
      let response = await fetch(
        `https://bridgegatebackend.herokuapp.com/api/trade/${
          this.state.user.user ? this.state.user.user.user._id : ""
        }`
      );
      let data = await response.json();
      this.setState({
        orders: data,
      });
    })();

    (async () => {
      let response = await fetch(
        `https://bridgegatebackend.herokuapp.com/allUser`
      );
      let data = await response.json();
      console.log(data);

      this.setState({
        allUsers: data,
      });
    })();

    (async () => {
      let response = await fetch(
        `https://bridgegatebackend.herokuapp.com/allWithdraw`
      );
      let data = await response.json();

      console.log(data);
      this.setState({
        allWithdraw: data,
      });
    })();

    (async () => {
      let response = await fetch(
        `https://bridgegatebackend.herokuapp.com/allDeposit`
      );
      let data = await response.json();
      console.log(data);

      this.setState({
        allDeposit: data,
      });
    })();

    (async () => {
      let response = await fetch(
        `https://bridgegatebackend.herokuapp.com/allTrade`
      );

      let data = await response.json();
      console.log(data);

      this.setState({
        allTrades: data,
      });
    })();

    (async () => {
      let response = await fetch(
        `https://bridgegatebackend.herokuapp.com/allVerify`
      );
      let data = await response.json();
      console.log(data);

      this.setState({
        allVerify: data,
      });
    })();
    (async () => {
      let response = await fetch(
        `https://bridgegatebackend.herokuapp.com/api/trade/${
          this.state.user.user ? this.state.user.user.user._id : ""
        }`
      );
      let data = await response.json();
      this.setState({
        orders: data,
      });
    })();

    (async () => {
      let response = await fetch(
        `https://bridgegatebackend.herokuapp.com/api/trade/user/${
          this.state.user.user ? this.state.user.user.user._id : ""
        }`
      );
      let user = await response.json();

      this.setState({
        user: user,
      });
    })();

    if (this.props.user.user) {
      const script3 = document.createElement("script");
      script3.src = "https://cdn.jsdelivr.net/gh/codabae/hostjs/dashUPPER15.js";
      script3.async = true;

      this.myRef3.current.appendChild(script3);
    }
  }

  render() {
    if (this.props.user.length === 0) {
      return <Redirect to="/" />;
    } else {
      // this.fav.href =  this.web.siteFav
      // this.title.innerHTML = this.web.siteTitle
      // console.log(this.state.orderIsh);

      return (
        <div ref={this.myRef3}>
          {/* Beggining of navbar */}

          {/* Ending of navbar */}
          {/* Beginning of contents */}
          <section className="dash-contents" onClick={this.closeForex}>
            <div className="dash-row">
              <Sidebar user={this.props.user} site={this.props.site} />

              {/* start route */}

              {/* start route */}
              {this.state.admin ? (
                <div
                  className="full-width dash-row"
                  style={{
                    paddingLeft: 0,
                    paddingBottom: 35,
                    overflow: "hidden",
                  }}
                >
                  <AdminSidebar />

                  <div className="admin-content">
                    <div className="public-card">
                      <div className="each-row dash-row">
                        <div className="dtls">
                          <h4>Username</h4>
                        </div>
                        <div className="actions">
                          <input
                            className="dash-input"
                            type="text"
                            name="text"
                          />
                        </div>
                      </div>

                      <div className="each-row dash-row">
                        <div className="dtls">
                          <h4>Profit (in percentage)</h4>
                        </div>
                        <div className="actions">
                          <input
                            className="dash-input"
                            type="text"
                            name="text"
                          />
                        </div>
                      </div>

                      <div className="each-row dash-row">
                        <div className="dtls">
                          <h4>Subscription Fee</h4>
                        </div>
                        <div className="actions">
                          <input
                            className="dash-input"
                            type="text"
                            name="text"
                          />
                        </div>
                      </div>

                      <div className="save-btn">
                        <button>Add</button>
                      </div>
                    </div>
                    <div className=" new-public-card">
                      <div>
                        <div>
                          <h4>Username :</h4>
                          <input
                            className="dash-input"
                            type="text"
                            name="text"
                          />
                        </div>

                        <div>
                          <h4>profit : </h4>
                          <input
                            className="dash-input"
                            type="text"
                            name="text"
                          />
                        </div>

                        <div>
                          <h4>Subscription: </h4>
                          <input
                            className="dash-input"
                            type="text"
                            name="text"
                          />
                        </div>
                      </div>
                      <div className="save-btn">
                        <button>Edit</button>
                      </div>
                    </div>
                  </div>
                </div>
              ) : (
                ""
              )}

              {/* {this.state.admin ? () : ''

             } */}

              {/* storrrrrp */}
            </div>
          </section>
        </div>
      );
    }
  }
}

export default AutoTrading;
