import React, { useState } from "react";
import Favicon from "react-favicon";
import { Eye, EyeSlash } from "react-bootstrap-icons";

import { Link } from "react-router-dom";
import ImageCarousel from "../Component/ImageCarousel/ImageCarousel";
import { Container, Row, Col, Form, Button } from "react-bootstrap";
import Header from "../Component/Header/Header";
import Footer from "../Component/Footer/Footer";

import { countryList } from "../Component/CountryList/CountryList";
import "../AccountsAsset/form.css";

export default function SignUp(props) {
  const [passwordShown, setPasswordShown] = useState(false);
  const [passwordShown1, setPasswordShown1] = useState(false);

  let web = props.web.web;
  let web2 = props.web;

  let savedSite = JSON.parse(localStorage.getItem("site"))
    ? JSON.parse(localStorage.getItem("site"))
    : [];

  const togglePasswordVisiblity = () => {
    setPasswordShown(passwordShown ? false : true);
  };

  const togglePasswordVisiblity1 = () => {
    setPasswordShown1(passwordShown1 ? false : true);
  };

  return (
    <div>
      <Favicon url={web ? web.siteFav : ""} />
      <Favicon url={web2 ? web2.siteFav : ""} />

      <div>
        <Header 
          web={web} 
          savedSite={savedSite} 
          url="/"
          title="Login"
        />
        <section className="auth-wrapper">
          <h1>Sign Up</h1>
          <div className="auth-form">
            <Form className="mt-4 pt-2" onSubmit={props.handleSubmitR}>
              {props.error ? (
                props.error.length > 0 ? (
                  <p className="error1">{props.error}</p>
                ) : (
                  props.error
                )
              ) : (
                ""
              )}
              <Form.Group
                controlId="formBasicText"
                className="floating mb-0"
              >
                <Form.Control
                  type="text"
                  name="name"
                  onChange={props.handle4R}
                  style={{ borderBottom: "1px solid #d8dbe4" }}
                  placeholder="Your First Name"
                />
              </Form.Group>
              <Form.Group
                controlId="formBasicText"
                className="floating mb-0"
              >
                <Form.Control
                  type="text"
                  name="lastname"
                  onChange={props.handle5R}
                  style={{ borderBottom: "1px solid #d8dbe4" }}
                  placeholder="Your Last Name"
                />
              </Form.Group>

              <Form.Group
                controlId="formBasicEmail"
                className="floating mb-0"
              >
                <Form.Control
                  type="email"
                  name="email"
                  onChange={props.handle1R}
                  style={{ borderBottom: "1px solid #d8dbe4" }}
                  placeholder="Your e-mail address"
                />
              </Form.Group>

              <Form.Group
                controlId="exampleForm.ControlSelect1"
                className="floating mb-0"
              >
                <Form.Control
                  as="select"
                  name="country"
                  onChange={props.handle7R}
                >
                  <option>Your Country</option>
                  {countryList.map((country) => (
                    <option>{country}</option>
                  ))}
                </Form.Control>
              </Form.Group>
              <Form.Group
                controlId="formBasicText"
                className="floating mb-0"
              >
                <Form.Control
                  type="number"
                  name="phonenumber"
                  onChange={props.handle6R}
                  style={{ borderBottom: "1px solid #d8dbe4" }}
                  placeholder="Your Phone Number"
                />
              </Form.Group>

              <Form.Group
                controlId="formBasicPassword"
                className="floating mb-0"
              >
                <Form.Control
                  type={passwordShown ? "text" : "password"}
                  name="password"
                  onChange={props.handle2R}
                  style={{ borderBottom: "1px solid #d8dbe4" }}
                  placeholder="Your password"
                />
                {passwordShown ? (
                  <EyeSlash
                    className="eye"
                    onClick={togglePasswordVisiblity}
                  />
                ) : (
                  <Eye
                    className="eye"
                    onClick={togglePasswordVisiblity}
                  />
                )}
              </Form.Group>
              <Form.Group
                controlId="formBasicPasswordTwo"
                className="floating mb-0"
              >
                <Form.Control
                  type={passwordShown1 ? "text" : "password"}
                  name="repeatPassword"
                  onChange={props.handle3R}
                  style={{ borderBottom: "1px solid #d8dbe4" }}
                  placeholder="Confirm password"
                />
                {passwordShown1 ? (
                  <EyeSlash
                    className="eye"
                    onClick={togglePasswordVisiblity1}
                  />
                ) : (
                  <Eye
                    className="eye"
                    onClick={togglePasswordVisiblity1}
                  />
                )}
              </Form.Group>

              <Form.Group
                controlId="formBasicCheckbox"
                className="d-flex mt-2 mb-2"
              >
                <Form.Check type="checkbox" name="agree" value={true} />
                <Form.Label className="agree">
                  I agree to the <Link to="/">terms of services</Link>
                </Form.Label>
              </Form.Group>
              <div className="pb-3">
                <Button
                  variant="primary"
                  style={{ background: web ? web.yourMainColor : "" }}
                  type="submit"
                  className="w-100"
                >
                  LET'S GO !
                </Button>
              </div>
            </Form>
            <div className="signup text-center">
              <p className="forget">
                Already have an account? 
                <Link to="/">
                  &nbsp; Login
                </Link>
              </p>
            </div>
            <div className="risk-taking text-center">
              <p className="mb-0">Risk Taking</p>
              <div className="text-left">
                <span>All trading involves risk. Only risk capital you're prepared to lose.</span>
              </div>
            </div>
          </div>
        </section>
        <Footer />
      </div>
    </div>
  );
}
