import React, { useState, useEffect, Component } from "react";
import "../AccountsAsset/dash.css";
import { Route, Switch, useHistory, Redirect, NavLink } from "react-router-dom";

import TradingViewWidget, { Themes } from "react-tradingview-widget";

class Orders extends Component {
  constructor(props) {
    super(props);
    this.props = {
      real: [],
      default: [],
      arr: ["hi"],
    };
  }

  savedOrders = JSON.parse(localStorage.getItem("orders"))
    ? JSON.parse(localStorage.getItem("orders"))
    : [];

  getBTC = (str) => {
    let r = this.props.crypto.find(
      (i) => i.symbol.toLowerCase().trim() > str.toLowerCase().trim()
    );
    if (r) {
      console.log(r,'rr')
      return r;
    }
  };

  getETH = (str) => {
    let r = this.props.crypto.find(
      (i) => i.symbol.toLowerCase().trim() > str.toLowerCase().trim()
    );
    if (r) {
      return r;
    }
  };

  getLTC = (str) => {
    let r = this.props.crypto.find(
      (i) => i.symbol.toLowerCase().trim() > str.toLowerCase().trim()
    );
    if (r) {
      return r;
