   const saveData=async()=>{
    setSubmitLoading(true)
  const res = await axios.put(
    `${endpoint}/api/site/mailsettings`,
    {
      mailEngine :mailEngine,
      SMTPServer:SMTPServer,
      SMTPPort:SMTPPort,
      SMTPMail:SMTPMail,
      SMTPPassword:SMTPPassword,
      EmailSenderName:EmailSenderName,
      mailForm :mailForm,
      emailSendName :emailSendName,
      supportMail :supportMail,
      supportPhone :supportPhone,
      supportAddress :supportAddress,
      DPOPhone :DPOPhone,
      DPOEmail :DPOEmail,
      sendWelcomeMail :sendWelcomeMail,
      welcomeMail :welcomeMail,
      newUserWelcomeMailTitle :newUserWelcomeMailTitle,
     }
 );

 const{response}=res
 console.log(response)
   if (res.data) {
    message.success('Settings successfully updated');
    setSubmitLoading(false)
  }
  else(message.error("Settings update failed"))
  }
  useEffect(()=>{
    if (props.site) {
      console.log(props.site.site)
      setmailEngine(props.site.site[0].mailEngine)
      setSMTPPort(props.site.site[0].SMTPPort)
      setSMTPServer(props.site.site[0].SMTPServer)
      setSMTPMail(props.site.site[0].SMTPMail)
      setSMTPPassword(props.site.site[0].SMTPPassword)
      setEmailSenderName(props.site.site[0].EmailSenderName)
      setmailForm(props.site.site[0].mailForm)
      setemailSendName(props.site.site[0].emailSendName)
      setsupportMail(props.site.site[0].supportMail)
      setsupportPhone(props.site.site[0].supportPhone)    
      setsupportAddress(props.site.site[0].supportAddress)
      setDPOPhone(props.site.site[0].DPOPhone)
      setDPOEmail(props.site.site[0].DPOEmail)
      setsendWelcomeMail(props.site.site[0].sendWelcomeMail)
      setwelcomeMail(props.site.site[0].welcomeMail)
      setnewUserWelcomeMailTitle(props.site.site[0].newUserWelcomeMailTitle,)

}
