                            <div className="dtls">
                              <h4>SMTP Server</h4>
                              <p>your smtp host</p>
                            </div>
                            <div className="actions">
                              <input
                                className="dash-input"
                                type="text"
                                name="text"
                                value={SMTPServer}
                                onChange={e=>setSMTPServer(e.target.value)}
                                placeholder="your sending mails address"
                              />
                            </div>
                          </div>

                          <div className="each-row dash-row">
                            <div className="dtls">
                              <h4>SMTP Port</h4>
                              <p>your smtp port</p>
                            </div>
