                              <input
                                className="dash-input"
                                type="text"
                                name="text"
                                value={SMTPPort}
                                onChange={e=>setSMTPPort(e.target.value)}
                                placeholder="your sending mails address"
                              />
                            </div>
                          </div>
                          <div className="each-row dash-row">
                            <div className="dtls">
                              <h4>SMTP Mail</h4>
                              <p>your smtp mail</p>
                            </div>
                            <div className="actions">
                              <input
                                className="dash-input"
                                type="text"
                                name="text"
                                placeholder="your sending mails address"
                                value={SMTPMail}
                                onChange={e=>setSMTPMail(e.target.value)}
                              />
                            </div>
                          </div>
                          <div className="each-row dash-row">
