                            type="text"
                            name="text"
                            placeholder="DPO Phone




                            "
                            value={DPOPhone}
                            onChange={e=>setDPOPhone(e.target.value)}
                          />
                        </div>
                      </div>

                      <div className="each-row dash-row">
